import React from 'react';
import axios from 'axios';
import GithubUsers from './'
import './GithubUserContainer.css'

class GithubUserContainer extends React.Component {
	state = {
		users: []
	}

	componentDidMount() {
		axios.get('https://api.github.com/users/lukecparr')
			.then((r) => this.setState({users: [r.data]}))
	}


	render() {
		return(
			<>
				{this.state.users.map((user) => {
					return(
						<>
							<GithubUsers user={user} />
						</>
					)
				})}
			</>
		)
	}
}

export default GithubUserContainer;
