import React from 'react';
import axios from 'axios';

class GithubUserContainer extends React.Component {
	state = {
		users: []
	}

	componentDidMount() {
		axios.get('https://api.github.com/users/lukecparr')
			.then((r) => this.setState({users: [r.data]}))
	}


	render() {
		return(
			<>
				{this.state.users.map((user) => {
					return(
						<div>
							<img src={user.avatar_url} alt="user photo"/>
							<div>
								<h3>{user.name}</h3>
								<p>{user.login}</p>
								<p>{user.location}</p>
								<p>Profile:</p>
								<a href={user.html_url}><p>{user.html_url}</p></a>
								<p>{user.followers}</p>
								<p>{user.following}</p>
								<p>{user.bio}</p>
							</div>
						</div>
					)
				})}
			</>
		)
	}
}

export default GithubUserContainer;
